mapper.select:

select

  distinct

'false' as QUERYID,

from adv_info_cpa




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

SELECT ID AS 'id', CPA_NAME AS cpaName, `STATUS` AS 'status', a.*, a.EFF_TIME AS effTime, a.EXP_TIME AS expTime, a.UPDATE_TIME AS updateTime, a.OPER_USER_NAME AS operUserName, a.OPER_USER_ID AS operUserId, a.PRICE AS price FROM adv_info_cpa a WHERE 1 = 1
AND a.CPA_NAME LIKE #{queryparam}
AND a.`STATUS` = #{status}
AND a.`STATUS` != 12
ORDER BY `STATUS` ASC,UPDATE_TIME DESC
LIMIT #{limitstart} ,#{limitend}

SELECT count(*) FROM adv_info_cpa a WHERE 1 = 1
AND a.CPA_NAME LIKE #{queryparam}
AND a.`STATUS` = #{status}
AND a.`STATUS` != 12

SELECT a.EFF_TIME AS starttime, a.EXP_TIME AS endtime, a.ID AS campaignid, a.CPA_NAME AS campaignname, b.BUNDLE_ID AS pkgname, a.PRICE AS price FROM adv_info_cpa a LEFT JOIN appstore_info b ON a.APPSTORE_INFO_ID = b.ID WHERE
a.EFF_TIME >= #{startTime} AND a.EFF_TIME <= #{endTime}
AND a.CPA_NAME LIKE concat_ws('','%',#{campaignname},'%')
LIMIT #{limitstart} ,#{limitend}

SELECT count(a.id) FROM adv_info_cpa a LEFT JOIN appstore_info b ON a.APPSTORE_INFO_ID = b.ID WHERE
a.EFF_TIME > #{startTime} AND a.EFF_TIME < #{endTime}
AND a.CPA_NAME LIKE concat_ws('','%',#{campaignname},'%')
