UPDATE_TIME, CPA_MANAGER_NAME, OLD_MOPAN_ID
)
values (#{id,jdbcType=INTEGER}, #{cpaName,jdbcType=VARCHAR},
#{cpaManager,jdbcType=INTEGER},
#{operUser,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{bizType,jdbcType=INTEGER},
#{updateTime,jdbcType=TIMESTAMP}, #{cpaManagerName,jdbcType=VARCHAR},
#{oldMopanId,jdbcType=INTEGER}
)
