mapper.select:

select

  distinct

'false' as QUERYID,

from channel_info_v2




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

select
from channel_info_v2 where STATUS='1' and (FULL_NAME = #{fullName,jdbcType=VARCHAR} or NAME = #{name,jdbcType=VARCHAR} or SIGN = #{sign,jdbcType=VARCHAR})
and ID != #{id,jdbcType=INTEGER}

select

from channel_info_v2
where STATUS='1' AND ID=#{id,jdbcType=INTEGER}

  	select * 
  	from channel_info_v2
  	where 1=1



			AND  MP_BUSINESS_NAME in  ${channelNames}


			AND  CHANNEL_DEPARTMENT =  #{teamId}



			AND STATUS = #{status}


			AND FULL_NAME like #{fullName}


			AND id = #{id}


			order by  ${orderByName}


			LIMIT #{LimitStart} , #{LimitEnd}

  	select count(*)
  	from (
  		select * 
  	from channel_info_v2
  	where 1=1



			AND  MP_BUSINESS_NAME in  ${channelNames}


			AND  CHANNEL_DEPARTMENT =  #{teamId}



			AND STATUS = #{status}


			AND FULL_NAME like #{fullName}


			AND id = #{id}


			order by  ${orderByName}

  	) w
