mapper.select:

select

	distinct

'false' as QUERYID,

from channel_interface




	order by ${orderByClause}


	limit #{limitStart} , #{limitEnd}

SELECT i.ID ID, i.`NAME` `NAME`, c.SHORT_NAME CHANNEL_SHORT_NAME, i.CHANNEL_DIRECTOR CHANNEL_DIRECTOR, c.CONTACT_NAME CHANNEL_CONTACT_NAME, c.CONTACT_PHONE CHANNEL_CONTACT_PHONE, i.CLICK_REPORT_URL CLICK_REPORT_URL, i.RETURN_CODE RETURN_CODE, i.CALLBACK_URL CALLBACK_URL, i.IDFA_STYLE IDFA_STYLE, i.MAC MAC, c.id CHANNEL_ID FROM channel_interface i LEFT JOIN channel_info c ON i.CHANNEL_ID = c.ID where 1=1
and i.name like #{param}
and i.id = #{id}
ORDER BY i.id DESC
limit #{limitStart} , #{limitEnd}

select count(w.id) from ( SELECT i.ID ID, i.`NAME` `NAME`, c.SHORT_NAME CHANNEL_SHORT_NAME, i.CHANNEL_DIRECTOR CHANNEL_DIRECTOR, c.CONTACT_NAME CHANNEL_CONTACT_NAME, c.CONTACT_PHONE CHANNEL_CONTACT_PHONE FROM channel_interface i LEFT JOIN channel_info c ON i.CHANNEL_ID = c.ID where 1=1
and i.name like #{param}
and i.id = #{id}
) w
