mapper.select:

select

	distinct

'false' as QUERYID,

from developer_apps




	order by ${orderByClause}


	limit #{limitStart} , #{limitEnd}

SELECT
count(a.ID)
FROM
developer_apps a
LEFT JOIN developer_info b ON a.OWNER_ID = b.ID
LEFT JOIN sys_media_category c on a.APP_TYPE=c.ID
WHERE
1 = 1

	AND a.APP_STATUS = #{status}


	AND a.APP_STATUS != 1 and a.APP_STATUS != 10





	AND a.AD_TYPE1 = 1


	AND a.AD_TYPE2 = 1


	AND a.AD_TYPE3 = 1


	AND (
	c.`DESC` LIKE #{param}
	OR a.ID LIKE #{param}
	OR a.APP_NAME LIKE #{param}
	OR b.NICKNAME LIKE #{param}
	OR b.REAL_NAME LIKE #{param}
	OR a.MP_BUSINESS_NAME LIKE #{param}
	)

SELECT `ID` as `id`, `APP_NAME` as appName FROM `developer_apps` a WHERE a.`OWNER_ID`=#{owner_id}
AND a.APP_STATUS=#{app_status}
order by `UPDATE_TIME` DESC
