  )
values (#{id,jdbcType=BIGINT}, #{timeid,jdbcType=INTEGER}, #{idfa,jdbcType=VARCHAR}, 
  #{appid,jdbcType=VARCHAR}, #{tryCount,jdbcType=INTEGER}, #{callAdvUrl,jdbcType=VARCHAR}, 
  #{responseMsg,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{sourceId,jdbcType=INTEGER}, 
