mapper.select:

select

  distinct

'false' as QUERYID,

from log_cas_call_source




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}


select 

from log_cas_call_source
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.delete:

delete from log_cas_call_source
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.select:
  	SELECT
  		CCCS.ID ID,
		CCCS.`STATUS` STATUS,
		CCCS.INSERT_TIME INSERT_TIME,
		CIV2.`NAME` CHANNEL_NAME,
		CIV2.ID CHANNEL_ID,
		CCCS.SOURCE_PRICE SOURCE_PRICE,
		CCCS.IDFA IDFA,
		CIIV2.NAME CPA,
		CIIV2.APPID APPID,
		CCCS.CALL_SOURCE_URL CALL_SOURCE_URL
	FROM
		LOG_CAS_CALL_SOURCE CCCS
	LEFT JOIN CHANNEL_INFO_V2 CIV2 ON CCCS.SOURCE_ID = CIV2.ID
	LEFT JOIN CP_INTERFACE_INFO_V2 CIIV2 ON CCCS.APPID = CIIV2.APPID
	WHERE
		1 = 1

			and CCCS.IDFA like #{idfa}


			and CCCS.SOURCE_PRICE >= #{beginSourcePrice}


			and CCCS.SOURCE_PRICE <= #{endSourcePrice}


			and CCCS.APPID = #{cpaid}


			and CCCS.STATUS = #{status}


			and CCCS.SOURCE_ID = #{sourceid}


			and CCCS.TIMEID	 IN 

             		#{item}  



			and CCCS.INSERT_TIME >= #{begintime}
			and CCCS.INSERT_TIME <= #{endtime}


	order by CCCS.INSERT_TIME desc
	LIMIT #{LimitStart} , #{LimitEnd}

	SELECT
		COUNT(W.ID)
	FROM
		(
		SELECT
  		CCCS.ID ID,
		CCCS.`STATUS` STATUS,
		CCCS.INSERT_TIME INSERT_TIME,
		CIV2.`NAME` CHANNEL_NAME,
		CIV2.ID CHANNEL_ID,
		CCCS.SOURCE_PRICE SOURCE_PRICE,
		CCCS.IDFA IDFA,
		CIIV2.NAME CPA,
		CIIV2.APPID APPID,
		CCCS.CALL_SOURCE_URL CALL_SOURCE_URL
	FROM
		LOG_CAS_CALL_SOURCE CCCS
	LEFT JOIN CHANNEL_INFO_V2 CIV2 ON CCCS.SOURCE_ID = CIV2.ID
	LEFT JOIN CP_INTERFACE_INFO_V2 CIIV2 ON CCCS.APPID = CIIV2.APPID
	WHERE
		1 = 1

			and CCCS.IDFA like #{idfa}


			and CCCS.SOURCE_PRICE >= #{beginSourcePrice}


			and CCCS.SOURCE_PRICE <= #{endSourcePrice}


			and CCCS.APPID = #{cpaid}


			and CCCS.STATUS = #{status}


			and CCCS.SOURCE_ID = #{sourceid}


			and CCCS.TIMEID	 IN 

             		#{item}  



			and CCCS.INSERT_TIME >= #{begintime}
			and CCCS.INSERT_TIME <= #{endtime}


		order by CCCS.INSERT_TIME desc
		) W
