mapper.select:

select

  distinct

'false' as QUERYID,

from log_cas_recv_adv_call




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}


select 

from log_cas_recv_adv_call
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.delete:

delete from log_cas_recv_adv_call
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.select:
SELECT a.`ID` AS `id`, a.IDFA AS idfa, a.FROM_IP AS fromIp, a.INSERT_TIME AS insertTime, a.CPA_PRICE AS cpaPrice, a.IS_ADV_IP AS isAdvIp, a.APPID AS appid, c.NAME AS cpaname, a.SOURCE_ID AS sourceid, b.NAME AS sourcename FROM log_cas_recv_adv_call a LEFT JOIN channel_info_v2 b ON a.SOURCE_ID = b.ID LEFT JOIN cp_interface_info_v2 c ON a.APPID = c.APPID WHERE 1=1
AND a.SOURCE_ID =#{sourceid}
AND a.APPID=#{cpaid}
AND a.IS_ADV_IP=#{isAdvIp}
AND a.IDFA like CONCAT('%',#{idfa},'%')
AND a.CPA_PRICE <= #{maxprice}
AND a.CPA_PRICE >= #{minprice}
AND a.TIMEID IN
#{item}
AND INSERT_TIME >=#{startDate} AND INSERT_TIME <= #{endDate}
ORDER BY a.INSERT_TIME DESC
LIMIT #{limitStart},#{limitEnd}

		SELECT
		  count(a.ID)
		FROM
		log_cas_recv_adv_call a
		LEFT JOIN channel_info_v2 b 
		ON a.SOURCE_ID = b.ID
		WHERE 1=1

				AND a.SOURCE_ID =#{sourceid}


				AND a.APPID=#{cpaid}


				AND a.IS_ADV_IP=#{isAdvIp}


				AND a.IDFA like CONCAT('%',#{idfa},'%')



			   AND a.CPA_PRICE <= #{maxprice}


			   AND a.CPA_PRICE >= #{minprice}

			 AND a.TIMEID IN 

             		#{item}  


				AND INSERT_TIME >=#{startDate} AND INSERT_TIME <= #{endDate}
