select 

from log_cas_user_click
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.delete:

delete from log_cas_user_click
where ID = #{id,jdbcType=BIGINT}
  and TIMEID = #{timeid,jdbcType=INTEGER}

mapper.select:
