mapper.select:

select

	distinct

'false' as QUERYID,

from fos_channel_month




	order by ${orderByClause}


	limit #{limitStart} , #{limitEnd}

SELECT OC.CHANNEL_ORDER_ID CHANNEL_ORDER_ID, OC.ORDER_ID ORDER_ID, O.ORDER_NAME ORDER_NAME, O.PRODUCT_NAME PRODUCT_NAME, O.DIRECTOR_NAME ORDER_DIRECTOR_NAME, O.CP_MAIN CP_MAIN, O.RELATION_ID RELATION_ID, OC.CHANNEL_ID CHANNEL_ID, C.NAME CHANNEL_NAME, C.MP_BUSINESS_NAME CHANNEL_DIRECTOR_NAME,
SUM(facd.FINANCIAL_SUBMIT_CCOUNT) C_COUNT_SUM, SUM(facd.FINANCIAL_SUBMIT_CAMOUNT) C_AMOUNT_SUM,
#{entryBegintime} ENTRY_BEGINTIME, #{entryEndtime} ENTRY_ENDTIME FROM FOS_OC_DAY OC LEFT JOIN fos_audit_channel_day facd on oc.id=facd.OC_DAY_ID LEFT JOIN FOS_ORDER O ON OC.ORDER_ID = O.ORDER_ID LEFT JOIN CHANNEL_INFO_V2 C ON OC.CHANNEL_ID = C.ID WHERE 1 = 1 AND OC.ENTRY_DAY_STATUS = 1 AND OC.OC_DAY_STATUS = 0 AND OC.ENTRY_TIME >= #{entryBegintime} AND OC.ENTRY_TIME <= #{entryEndtime} AND facd.FINANCIAL_SUBMIT_CHANNEL_STATUS = 3 AND OC.CHANNEL_ORDER_ID NOT IN ( SELECT CHANNEL_ORDER_ID FROM FOS_CHANNEL_MONTH WHERE ENTRY_BEGINTIME = #{entryBegintime} AND ENTRY_ENDTIME = #{entryEndtime} )
AND oc.ORDER_DIRECTOR_NAME in ${businessNames}
AND oc.CHANNEL_DIRECTOR_NAME in ${channelNames}
AND o.ORDER_DEPARTMENT = #{teamId}
AND O.ORDER_NAME like #{orderName}
AND O.PRODUCT_NAME like #{productName}
AND C.NAME like #{channelName}
GROUP BY OC.CHANNEL_ORDER_ID order by OC.CHANNEL_ORDER_ID desc LIMIT #{LimitStart} , #{LimitEnd}

select count(w.CHANNEL_ORDER_ID) from ( SELECT OC.CHANNEL_ORDER_ID CHANNEL_ORDER_ID, OC.ORDER_ID ORDER_ID, O.ORDER_NAME ORDER_NAME, O.PRODUCT_NAME PRODUCT_NAME, O.DIRECTOR_NAME ORDER_DIRECTOR_NAME, O.CP_MAIN CP_MAIN, O.RELATION_ID RELATION_ID, OC.CHANNEL_ID CHANNEL_ID, C.NAME CHANNEL_NAME, C.MP_BUSINESS_NAME CHANNEL_DIRECTOR_NAME,
SUM(facd.FINANCIAL_SUBMIT_CCOUNT) C_COUNT_SUM, SUM(facd.FINANCIAL_SUBMIT_CAMOUNT) C_AMOUNT_SUM,
#{entryBegintime} ENTRY_BEGINTIME, #{entryEndtime} ENTRY_ENDTIME FROM FOS_OC_DAY OC LEFT JOIN fos_audit_channel_day facd on oc.id=facd.OC_DAY_ID LEFT JOIN FOS_ORDER O ON OC.ORDER_ID = O.ORDER_ID LEFT JOIN CHANNEL_INFO_V2 C ON OC.CHANNEL_ID = C.ID WHERE 1 = 1 AND OC.ENTRY_DAY_STATUS = 1 AND OC.OC_DAY_STATUS = 0 AND OC.ENTRY_TIME >= #{entryBegintime} AND OC.ENTRY_TIME <= #{entryEndtime} AND facd.FINANCIAL_SUBMIT_CHANNEL_STATUS = 3 AND OC.CHANNEL_ORDER_ID NOT IN ( SELECT CHANNEL_ORDER_ID FROM FOS_CHANNEL_MONTH WHERE ENTRY_BEGINTIME = #{entryBegintime} AND ENTRY_ENDTIME = #{entryEndtime} )
AND oc.ORDER_DIRECTOR_NAME in ${businessNames}
AND oc.CHANNEL_DIRECTOR_NAME in ${channelNames}
AND o.ORDER_DEPARTMENT = #{teamId}
AND O.ORDER_NAME like #{orderName}
AND O.PRODUCT_NAME like #{productName}
AND C.NAME like #{channelName}
GROUP BY OC.CHANNEL_ORDER_ID order by OC.CHANNEL_ORDER_ID desc ) w

SELECT
CM.*, OC.ADVANCE_PAY ADVANCE_PAY,CIV2.FULL_NAME FULL_NAME
FROM
FOS_CHANNEL_MONTH CM
LEFT OUTER JOIN FOS_ORDER_CHANNEL OC ON CM.CHANNEL_ORDER_ID =
OC.CHANNEL_ORDER_ID
LEFT OUTER JOIN channel_info_v2 CIV2 ON CM.CHANNEL_ID=CIV2.ID
WHERE
1=1

	AND CM.ORDER_NAME like #{orderName}


	AND CM.PRODUCT_NAME like #{productName}


	AND CM.CHANNEL_NAME like #{channelName}


	AND CM.ID = #{id}


	AND CM.ENTRY_BEGINTIME = #{entryBegintime}


	AND CM.ENTRY_ENDTIME = #{entryEndtime}


	AND CM.STATUS = #{status}


	AND CM.ENTRY_MONEY = #{entryMoney}


	AND CM.ENTRY_TICKET = #{entryTicket}


	ORDER BY #{OrderByClause}

LIMIT #{LimitStart} , #{LimitEnd}

SELECT COUNT(W.ID)
FROM(
SELECT
CM.*, OC.ADVANCE_PAY ADVANCE_PAY,CIV2.FULL_NAME FULL_NAME
FROM
FOS_CHANNEL_MONTH CM
LEFT OUTER JOIN FOS_ORDER_CHANNEL OC ON CM.CHANNEL_ORDER_ID =
OC.CHANNEL_ORDER_ID
LEFT OUTER JOIN channel_info_v2 CIV2 ON CM.CHANNEL_ID=CIV2.ID
WHERE
1=1

	AND CM.ORDER_NAME like #{orderName}


	AND CM.PRODUCT_NAME like #{productName}


	AND CM.CHANNEL_NAME like #{channelName}


	AND CM.ID = #{id}


	AND CM.ENTRY_BEGINTIME = #{entryBegintime}


	AND CM.ENTRY_ENDTIME = #{entryEndtime}


	AND CM.STATUS = #{status}


	AND CM.ENTRY_MONEY = #{entryMoney}


	AND CM.ENTRY_TICKET = #{entryTicket}


	ORDER BY #{OrderByClause}

) W

SELECT a.`ID` AS id, a.`CHANNEL_ORDER_ID` AS channelOrderId, a.`ORDER_ID` AS orderId, a.`ORDER_NAME` AS orderName, a.`PRODUCT_NAME` AS productName, a.`ORDER_DIRECTOR_NAME` AS orderDirectorName, a.`CP_MAIN` AS cpMain, a.`RELATION_ID` AS relationId, a.`CHANNEL_ID` AS channelId, a.`CHANNEL_NAME` AS channelName, a.`CHANNEL_DIRECTOR_NAME` AS channelDirectorName, a.`C_UNIT` AS cUnit, a.`C_COUNT` AS cCount, a.`C_AMOUNT` AS cAmount, a.`SUB_AMOUNT` AS subAmount, a.`C_COUNT_SUM` AS cCountSum, a.`C_AMOUNT_SUM` AS cAmountSum, a.`MEDIA_REBATE` AS mediaRebate, a.`OTHER_EXPENSE` AS otherExpense, a.`CHANNEL_CUT` AS channelCut, a.`ENTRY_MONTH_STATUS` AS entryMonthStatus, a.`ENTRY_MONEY` AS entryMoney, a.`ENTRY_TICKET` AS entryTicket, a.`ENTRY_BEGINTIME` AS entryBegintime, a.`ENTRY_ENDTIME` AS entryEndtime, a.`STATUS` AS STATUS, a.`CREATE_TIME` AS createTime, a.`MODIFY_TIME` AS modifyTime, a.`RECEIVE_ID` AS receiveId, b.`ID` AS auditChannelMonthId, b.`FOS_CHANNEL_MONTH_ID` AS fosChannelMonthId, b.`CHANNEL_SUBMIT_CONFIRM` AS channelSubmitConfirm, b.`CHANNEL_UPDATE_C_COUNT_SUM` AS channelUpdateCCountSum, b.`CHANNEL_UPDATE_C_AMOUNT_SUM` AS channelUpdateCAmountSum, b.`CHANNEL_UPDATE_MEDIA_REBATE` AS channelUpdateMediaRebate, b.`CHANNEL_UPDATE_OTHER_EXPENSE` AS channelUpdateOtherExpense, b.`CHANNEL_UPDATE_CHANNEL_CUT` AS channelUpdateChannelCut, b.`FINANCIAL_SUBMIT_CHANNEL_STATUS` AS financialSubmitChannelStatus, b.`FINANCIAL_SUBMIT_C_COUNT_SUM` AS financialSubmitCCountSum, b.`FINANCIAL_SUBMIT_C_AMOUNT_SUM` AS financialSubmitCAmountSum, b.`FINANCIAL_SUBMIT_MEDIA_REBATE` AS financialSubmitMediaRebate, b.`FINANCIAL_SUBMIT_OTHER_EXPENSE` AS financialSubmitOtherExpense, b.`FINANCIAL_SUBMIT_CHANNEL_CUT` AS financialSubmitChannelCut, b.`FINANCIAL_SUBMIT_CHANNEL_DATE` AS financialSubmitChannelDate, b.`FINANCIAL_SUBMIT_CHANNEL_NOTTHROUGH_TIME` AS financialSubmitChannelNotthroughTime, b.`FINANCIAL_SUBMIT_CHANNEL_NOTTHROUGH_REASON` AS financialSubmitChannelNotthroughReason, b.`RECEIVE_BILL_STATUS` AS receiveBillStatus, c.ADVANCE_PAY AS advancePayTotal, d.EXTEND_TYPE AS extendType, e.FULL_NAME AS channelFullName, d.ORDER_DEPARTMENT orderDepartment FROM fos_channel_month a LEFT JOIN fos_audit_channel_month b ON a.ID = b.FOS_CHANNEL_MONTH_ID LEFT JOIN fos_order_channel c ON c.CHANNEL_ORDER_ID = a.CHANNEL_ORDER_ID LEFT JOIN fos_order d ON d.ORDER_ID = a.ORDER_ID LEFT JOIN channel_info_v2 e ON e.ID = a.CHANNEL_ID WHERE 1=1
AND a.ORDER_DIRECTOR_NAME in ${businessNames}
AND a.CHANNEL_DIRECTOR_NAME in ${channelNames}
AND d.ORDER_DEPARTMENT = #{teamId}
AND d.ORDER_DEPARTMENT = #{orderDepartment}
AND b.FINANCIAL_SUBMIT_CHANNEL_STATUS = #{status}
AND b.RECEIVE_BILL_STATUS = #{receivebillstatus}
AND b.FINANCIAL_SUBMIT_CHANNEL_NOTTHROUGH_TIME = #{times}
AND a.ORDER_NAME LIKE concat_ws('','%',#{ordername},'%')
AND a.CHANNEL_NAME LIKE concat_ws('','%',#{channelname},'%')
ORDER BY a.ID DESC
LIMIT #{limitstart},#{limitend}

SELECT
	count(a.`ID`)
FROM
	fos_channel_month a
LEFT JOIN fos_audit_channel_month b ON a.ID = b.FOS_CHANNEL_MONTH_ID
LEFT JOIN fos_order d ON d.ORDER_ID = a.ORDER_ID
WHERE
	1=1



		AND  a.ORDER_DIRECTOR_NAME in  ${businessNames}


		AND  a.CHANNEL_DIRECTOR_NAME in  ${channelNames}


		AND  d.ORDER_DEPARTMENT =  #{teamId}



		AND  d.ORDER_DEPARTMENT =  #{orderDepartment}



		AND b.FINANCIAL_SUBMIT_CHANNEL_STATUS = #{status}


		AND b.RECEIVE_BILL_STATUS = #{receivebillstatus}


		AND b.FINANCIAL_SUBMIT_CHANNEL_NOTTHROUGH_TIME = #{times}


		AND a.ORDER_NAME LIKE concat_ws('','%',#{ordername},'%')


		AND a.CHANNEL_NAME LIKE concat_ws('','%',#{channelname},'%')
