mapper.select:

select

  distinct

'false' as QUERYID,

from fos_income_cost




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

SELECT FIC.ID id, FIC.ORDER_NAME orderName, FIC.CHANNEL_ORDER_ID channelOrderId, FIC.ORDER_ID orderId, FIC.PRODUCT_NAME productName, FIC.COOPERATE_NAME cooperateName, FIC.CHANNEL_ID channelId, FIC.CHANNEL_NAME channelName, FIC.FINANCIAL_INCOME financialSubmitOamount, FIC.FINANCIAL_COST financialSubmitCamount, FIC.EXTEND_TYPE extendType, FIC.MONEY_TAX moneyTax, FIC.PROFIT profit, FIC.PROFIT_MARGIN profitMargin, FIC.BUSINESS_TIME businessTime, o.ORDER_DEPARTMENT orderDepartment FROM FOS_INCOME_COST FIC left join fos_order o on fic.ORDER_ID=o.ORDER_ID WHERE 1=1
AND o.ORDER_DEPARTMENT = #{orderDepartment}
AND FIC.BUSINESS_TIME>= #{beginTime}
AND FIC.BUSINESS_TIME<= #{endTime}
AND FIC.ORDER_NAME like #{orderName}
AND FIC.CHANNEL_NAME like #{channelName}
LIMIT #{LimitStart} , #{LimitEnd}

select count(FIC.id)
FROM
	FOS_INCOME_COST FIC left join fos_order o on fic.ORDER_ID=o.ORDER_ID
WHERE 1=1	


	AND  o.ORDER_DEPARTMENT =  #{orderDepartment}



	AND FIC.BUSINESS_TIME>= #{beginTime}


	AND FIC.BUSINESS_TIME<= #{endTime}


	AND FIC.ORDER_NAME like #{orderName}


	AND FIC.CHANNEL_NAME like #{channelName}

SELECT	
	sum(FIC.FINANCIAL_INCOME) financialSubmitOamount,
	sum(FIC.FINANCIAL_COST) financialSubmitCamount,
	sum(FIC.PROFIT) profit,
	sum(FIC.MONEY_TAX) moneyTax
FROM
	FOS_INCOME_COST FIC left join fos_order o on fic.ORDER_ID=o.ORDER_ID
WHERE 1=1	


	AND  o.ORDER_DEPARTMENT =  #{orderDepartment}



	AND FIC.BUSINESS_TIME>= #{beginTime}


	AND FIC.BUSINESS_TIME<= #{endTime}


	AND FIC.ORDER_NAME like #{orderName}


	AND FIC.CHANNEL_NAME like #{channelName}
