mapper.select:

select

	distinct

'false' as QUERYID,

from fos_order_channel




	order by ${orderByClause}


	limit #{limitStart} , #{limitEnd}

SELECT
OC.CHANNEL_ORDER_ID CHANNEL_ORDER_ID,
OC.ORDER_ID ORDER_ID,
O.ORDER_NAME ORDER_NAME,
O.PAY_UNIT PAY_UNIT,
O.`STATUS` ORDER_STATUS,
OC.CHANNEL_ID CHANNEL_ID,
C.NAME CHANNEL_NAME,
C.STATUS CHANNEL_STATUS,
OC.`STATUS` `STATUS`,
OC.AUTOFLAG AUTOFLAG,
OC.OUTER_UNIT OUTER_UNIT,
OC.RELATION_ID RELATION_ID,
OC.ADVANCE_PAY ADVANCE_PAY,
OC.REMARK REMARK
FROM
FOS_ORDER_CHANNEL OC
LEFT JOIN FOS_ORDER O ON OC.ORDER_ID = O.ORDER_ID
LEFT JOIN CHANNEL_INFO_V2 C ON OC.CHANNEL_ID = C.ID
WHERE
1 = 1

	and OC.CHANNEL_ORDER_ID = #{channelOrderId}


	and O.ORDER_NAME like #{orderName}


	and C.NAME like #{channelName}


	and OC.ORDER_ID = #{orderId}


	and OC.STATUS = #{status}


	and C.STATUS = #{channelStatus}


	and O.STATUS = #{orderStatus}


	and OC.AUTOFLAG = #{autoFlag}


	and OC.RELATION_ID IS NOT NULL




	AND  o.DIRECTOR_NAME in  ${businessNames}


	AND  c.MP_BUSINESS_NAME in  ${channelNames}


	AND  o.ORDER_DEPARTMENT =  #{teamId}



order by OC.CREATE_TIME desc


	LIMIT #{LimitStart} , #{LimitEnd}

select count(w.CHANNEL_ORDER_ID)
from (
SELECT
OC.CHANNEL_ORDER_ID CHANNEL_ORDER_ID,
OC.ORDER_ID ORDER_ID,
O.ORDER_NAME ORDER_NAME,
O.PAY_UNIT PAY_UNIT,
O.`STATUS` ORDER_STATUS,
OC.CHANNEL_ID CHANNEL_ID,
C.NAME CHANNEL_NAME,
C.STATUS CHANNEL_STATUS,
OC.`STATUS` `STATUS`,
OC.AUTOFLAG AUTOFLAG,
OC.OUTER_UNIT OUTER_UNIT,
OC.RELATION_ID RELATION_ID,
OC.ADVANCE_PAY ADVANCE_PAY,
OC.REMARK REMARK
FROM
FOS_ORDER_CHANNEL OC
LEFT JOIN FOS_ORDER O ON OC.ORDER_ID = O.ORDER_ID
LEFT JOIN CHANNEL_INFO_V2 C ON OC.CHANNEL_ID = C.ID
WHERE
1 = 1

	and OC.CHANNEL_ORDER_ID = #{channelOrderId}


	and O.ORDER_NAME like #{orderName}


	and C.NAME like #{channelName}


	and OC.ORDER_ID = #{orderId}


	and OC.STATUS = #{status}


	and C.STATUS = #{channelStatus}


	and O.STATUS = #{orderStatus}


	and OC.AUTOFLAG = #{autoFlag}


	and OC.RELATION_ID IS NOT NULL




	AND  o.DIRECTOR_NAME in  ${businessNames}


	AND  c.MP_BUSINESS_NAME in  ${channelNames}


	AND  o.ORDER_DEPARTMENT =  #{teamId}


) w
