mapper.select:

select

  distinct

'false' as QUERYID,

from fos_order




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

    select
   		*
    from fos_order o
    where 1=1



			AND  o.DIRECTOR_NAME in  ${businessNames}


			AND  o.ORDER_DEPARTMENT =  #{teamId}



			AND o.ORDER_ID like #{orderId}


			AND o.order_name like #{orderName}


			AND o.product_name like #{productName}


			AND o.director_name like #{directorName}


			AND o.is_split like #{isSplit}


			AND o.status like #{status}


			ORDER BY o.${orderByName}


			LIMIT #{LimitStart} , #{LimitEnd}

    select count(*) 
    from (
    	select
   		*
    from fos_order o
    where 1=1



			AND  o.DIRECTOR_NAME in  ${businessNames}


			AND  o.ORDER_DEPARTMENT =  #{teamId}



			AND o.ORDER_ID like #{orderId}


			AND o.order_name like #{orderName}


			AND o.product_name like #{productName}


			AND o.director_name like #{directorName}


			AND o.is_split like #{isSplit}


			AND o.status like #{status}


			ORDER BY o.${orderByName}

    ) w
