mapper.select:

select

  distinct

'false' as QUERYID,

from fos_order_month_history




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

  	SELECT
		*
	FROM
		FOS_ORDER_MONTH_HISTORY
	WHERE
		1 = 1

			AND ID = #{id}


			AND ORDER_NAME like #{orderName}


			AND PRODUCT_NAME like #{productName}


			AND COOPERATE_NAME like #{cooperateName}


			AND ORDER_DIRECTOR_NAME like #{directorName}


			AND  STATUS_DRAW = #{statusDraw}


			AND  STATUS_RETURN = #{statusReturn}


			AND EXTEND_TYPE = #{extendType}


			AND ((BEGIN_TIME >= #{begintime} AND BEGIN_TIME <= #{endtime})
				OR (END_TIME >= #{begintime} AND END_TIME <= #{endtime}))


			ORDER BY
			${orderByName}


			LIMIT #{LimitStart} , #{LimitEnd}

SELECT COUNT(*)
FROM (
	SELECT
	*
FROM
	FOS_ORDER_MONTH_HISTORY
WHERE
	1 = 1

		AND ORDER_NAME like #{orderName}


		AND PRODUCT_NAME like #{productName}


		AND COOPERATE_NAME like #{cooperateName}


		AND ORDER_DIRECTOR_NAME like #{directorName}


		AND  STATUS_DRAW = #{statusDraw}


		AND  STATUS_RETURN = #{statusReturn}


		AND EXTEND_TYPE = #{extendType}


		AND ((BEGIN_TIME >= #{begintime} AND BEGIN_TIME <= #{endtime})
			OR (END_TIME >= #{begintime} AND END_TIME <= #{endtime}))


		ORDER BY
		${orderByName}


		LIMIT #{LimitStart} , #{LimitEnd}

)W

SELECT SUM(O_COUNT) O_COUNT, SUM(O_AMOUNT) O_AMOUNT, SUM(SUB_O_AMOUNT) SUB_O_AMOUNT, SUM(C_COUNT) C_COUNT, SUM(C_AMOUNT) C_AMOUNT, SUM(SUB_C_AMOUNT) SUB_C_AMOUNT, SUM(REAL_C_AMOUNT) REAL_C_AMOUNT, SUM(PROFIT) PROFIT, SUM(PROFIT_MARGIN) PROFIT_MARGIN, SUM(MONEY) MONEY, SUM(RETURN_MONEY) RETURN_MONEY FROM FOS_ORDER_MONTH_HISTORY WHERE 1 = 1
AND ORDER_NAME like #{orderName}
AND PRODUCT_NAME like #{productName}
AND COOPERATE_NAME like #{cooperateName}
AND ORDER_DIRECTOR_NAME like #{directorName}
AND STATUS_DRAW = #{statusDraw}
AND STATUS_RETURN = #{statusReturn}
AND EXTEND_TYPE = #{extendType}
AND ((BEGIN_TIME >= #{begintime} AND BEGIN_TIME <= #{endtime}) OR (END_TIME >= #{begintime} AND END_TIME <= #{endtime}))
