mapper.select:

select

  distinct

'false' as QUERYID,

from fos_receive_order




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

SELECT a.ORDER_ID AS orderId, a.CHANNEL_ID AS channelId, a.CHANNEL_ORDER_ID AS channelOrderId, b.ORDER_NAME AS orderName, b.PRODUCT_NAME AS productName, b.COOPERATE_NAME AS cooperateName, c.`NAME` AS channelName, c.FULL_NAME AS channelFullName, ( SUM(IFNULL(d.ADVANCE_PAY_CONSUME,0) ) + IFNULL(a.ADVANCE_PAY,0) ) AS advancePayTotal, a.CREATE_TIME AS createTime, IFNULL(a.ADVANCE_PAY,0) AS advancePayRemain FROM fos_order_channel a LEFT JOIN fos_order b ON a.ORDER_ID = b.ORDER_ID LEFT JOIN channel_info_v2 c ON a.CHANNEL_ID = c.ID LEFT JOIN fos_receive_order d ON a.CHANNEL_ORDER_ID = d.CHANNEL_ORDER_ID and d.RECEIVE_STATUS = 2 WHERE 1=1
AND b.DIRECTOR_NAME in ${businessNames}
AND c.MP_BUSINESS_NAME in ${channelNames}
AND b.ORDER_DEPARTMENT = #{teamId}
AND b.ORDER_NAME LIKE concat_ws('','%',#{ordername},'%')
AND c.FULL_NAME LIKE concat_ws('','%',#{channelname},'%')
GROUP BY a.CHANNEL_ORDER_ID ORDER BY a.CREATE_TIME DESC
LIMIT #{limitstart},#{limitend}

select sum(x.advancePayTotal) as advancePayTotal, sum(x.advancePayRemain) as advancePayRemain from (SELECT a.ORDER_ID AS orderId, a.CHANNEL_ID AS channelId, a.CHANNEL_ORDER_ID AS channelOrderId, b.ORDER_NAME AS orderName, b.PRODUCT_NAME AS productName, b.COOPERATE_NAME AS cooperateName, c.`NAME` AS channelName, c.FULL_NAME AS channelFullName, ( SUM(IFNULL(d.ADVANCE_PAY_CONSUME,0) ) + IFNULL(a.ADVANCE_PAY,0) ) AS advancePayTotal, a.CREATE_TIME AS createTime, IFNULL(a.ADVANCE_PAY,0) AS advancePayRemain FROM fos_order_channel a LEFT JOIN fos_order b ON a.ORDER_ID = b.ORDER_ID LEFT JOIN channel_info_v2 c ON a.CHANNEL_ID = c.ID LEFT JOIN fos_receive_order d ON a.CHANNEL_ORDER_ID = d.CHANNEL_ORDER_ID and d.RECEIVE_STATUS = 2 WHERE 1=1
AND b.DIRECTOR_NAME in ${businessNames}
AND c.MP_BUSINESS_NAME in ${channelNames}
AND b.ORDER_DEPARTMENT = #{teamId}
AND b.ORDER_NAME LIKE concat_ws('','%',#{ordername},'%')
AND c.FULL_NAME LIKE concat_ws('','%',#{channelname},'%')
GROUP BY a.CHANNEL_ORDER_ID) x

select fro.*
from fos_receive_order fro 
left join fos_order fo on fro.order_id=fo.order_id
where 1=1


	AND  fo.DIRECTOR_NAME in  ${businessNames}


	AND  fro.CHANNEL_DIRECTOR_NAME in  ${channelNames}


	AND  fo.ORDER_DEPARTMENT =  #{teamId}



	AND  fro.id =  #{id}


	AND  fro.CHANNEL_ORDER_NAME like  #{channelOrderName}


	AND  fro.ORDER_NAME like  #{orderName}


	AND  fro.CHANNEL_NAME like  #{channelName}


	AND  fro.status =  #{status}


	AND  fro.receive_status =  #{receiveStatus}


	order by   fro.${orderByName}



	LIMIT #{LimitStart} , #{LimitEnd}

select count(*)
from (
	select fro.*
from fos_receive_order fro 
left join fos_order fo on fro.order_id=fo.order_id
where 1=1


	AND  fo.DIRECTOR_NAME in  ${businessNames}


	AND  fro.CHANNEL_DIRECTOR_NAME in  ${channelNames}


	AND  fo.ORDER_DEPARTMENT =  #{teamId}



	AND  fro.id =  #{id}


	AND  fro.CHANNEL_ORDER_NAME like  #{channelOrderName}


	AND  fro.ORDER_NAME like  #{orderName}


	AND  fro.CHANNEL_NAME like  #{channelName}


	AND  fro.status =  #{status}


	AND  fro.RECEIVE_STATUS =  #{receiveStatus}


	order by   fro.${orderByName}

) w

